import React, { useRef, useState } from 'react';
import PostForm from './components/PostForm';
// import Counter from './components/Counter';
// import PostItem from './components/PostItem';
import PostList from './components/PostList';
import MyInput from './components/UI/input/MyInput';
import MySelect from './components/UI/select/MySelect';
import './styles/App.css';

function App() {
  const [posts, setPosts] = useState([
    {id: 1, title: 'aa 1', body: 'dd'},
    {id: 2, title: 'bb 2', body: 'cc'},
    {id: 3, title: 'cc 3', body: 'bb'},
    {id: 4, title: 'dd 4', body: 'aa'}
  ])

  const [selectedSort, setSelectedSort] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  function getSortedPosts() {
    console.log('Get Sorted Posts');
    console.log(posts);
    if (selectedSort) return [...posts].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]))
    return posts;
  }



  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id));
  }

  const sortedPosts = getSortedPosts();

  const sortPosts = (sort) => {
    setSelectedSort(sort);
    console.log(sort);
  }

  return (
    <div className='App'>
      <PostForm create={createPost}/>
      <hr style={{margin: '15px 0'}}/>
      <MyInput
        placeholder='Find post'
        value={searchQuery}
        onChange={e => searchQuery(e.target.value)}
      />
      <div>
        <MySelect
          value={selectedSort}
          onChange={sortPosts}
          defaultValue="Sort by"
          options={[
            {value: 'title', name: 'Sort by name'},
            {value: 'body', name: 'Sord by description'},
          ]}
        />
      </div>
      {posts.length !== 0
        ? <PostList remove={removePost} posts={sortedPosts} title={'Posts'}/>
        : <h1 style={{textAlign: 'center'}}>Posts not found!</h1>
      }
    </div>
  );
}

export default App;
