import React, { useState } from 'react';
import Counter from './components/Counter';
import PostItem from './components/PostItem';
import PostList from './components/PostList';
import MyButton from './components/UI/button/MyButton';
import MyInput from './components/UI/input/MyInput';
import './styles/App.css';

function App() {
  const [posts, setPosts] = useState([
    {id: 1, title: 'Javascript 1', description: 'Descritpion'},
    {id: 2, title: 'Javascript 2', description: 'Descritpion'},
    {id: 3, title: 'Javascript 3', description: 'Descritpion'},
    {id: 4, title: 'Javascript 4', description: 'Descritpion'}
  ])

  const [title, setTitle] = useState('');


  const addNewPost = (e) => {
    e.preventDefault();
  }

  return (
    <div className='App'>
      <form>
        <MyInput
          value={title}
          onChange = {e => setTitle(e.target.value)} 
          type='text' 
          placeholder='Post name'
        />
        <MyInput type='text' placeholder='Post description'/>
        <MyButton onClick={addNewPost}>Create Post</MyButton>
      </form>
      <PostList posts={posts} title={'Posts'}/>
    </div>
  );
}

export default App;
