const PostFilter = () => {
    return (
        <div>
            <MyInput
            placeholder='Find post'
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
        />
        <div>
            <MySelect
            value={selectedSort}
            onChange={sortPosts}
            defaultValue="Sort by"
            options={[
                {value: 'title', name: 'Sort by name'},
                {value: 'body', name: 'Sord by description'},
            ]}
            />
        </div>
        </div>

    );
};

export default PostFilter;
